import styled from 'styled-components';

export const HomeWidget=styled.div`
    margin:20px 10%;
    overflow:hidden;
`
export const HomeLeftWidget=styled.div`
    width:66%;
    margin:0 2%;
    float:left;
    .HomeImg{
        display:block;
        width:100%;
        height:280px;
        margin:0;
        box-shadow:2px 2px 8px #333333;
    }
`
export const HomeRightWidget=styled.div`
    width:30%;
    float:right;

`

// Topic
export const TopicWidget=styled.div`
    padding:20px 10px 0 0;
    overflow:hidden;
`
export const TopicItem=styled.div`
    
    height:32px;
    line-height:32px;
    border-radius:4px;
    color:#000;
    font-size:13px;
    background:#DBDBDB;
    padding-right:10px;
    .TopicItemImg{
        display:block;
        width:40px;
        height:32px;
        float:left;
        border:none;
        margin-right:10px;
    }
`
export const TopicItemBtn =styled.span`
    float:left;
    border:1px solid #dcdcdc;

`
