import React,{Component} from 'react';
import { connect } from 'react-redux';
import {
    TopicWidget,
    TopicItem,
} from '../style';
class Topic extends Component{
    render(){
        const {list} =this.props;
        return (
            <TopicWidget>
                {list.map((item)=>{
                    return(
                        //因为是immutable数据，所以要用get才能取出来
                        <TopicItem>
                            <img className='TopicItemImg' src='https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1555668475588&di=08fad851b211b7e55b4b58fd3e3968ab&imgtype=0&src=http%3A%2F%2Fbpic.588ku.com%2Felement_origin_min_pic%2F16%2F10%2F23%2F4a685054217e290eab6bfcc9c4f85014.jpg' alt=''/>
                            {item.get('title')}
                        </TopicItem>
                    )
                })}
                
            </TopicWidget>
        )
    }
}   

    const mapState=(state)=>({
        list:state.getIn(['Home','TopicList'])
    })

export default connect(mapState,null)(Topic);
