import * as constants from './constant';
const defaultState={
    Focus:false  
}
export default (state=defaultState,action)=>{
    if(action.type===constants.SHOW_SEARCH){
        return{
            Focus:true
        }
    }
    if(action.type===constants.ClOSE_SEARCH){
        return{
            Focus:false
        }
    }
    return state;
}
