import React,{Component} from 'react';
import {connect} from 'react-redux';
import {
    RecommendWidget,
    RecommendWidgetItem
 } from '../style';
class Recommend extends Component{

    render(){
        const {RecImgList} = this.props;
        return (
            <RecommendWidget>
                {
                    RecImgList.map((item)=>{
                        <RecommendWidgetItem>
                            <img className='RecImg' src='https://cdn2.jianshu.io/assets/web/collect-note-955d8c71641a360924390da9da4b0151.png' alt='' />
                        </RecommendWidgetItem>
                    })
                }
                

            </RecommendWidget>
        )
    }
}   
const mapState=(state)=>({
    RecImgList:state.getIn(['Home','RecImgList'])
})
export default connect(mapState,null)(Recommend);
