import React ,{ Component }from 'react';
import { connect } from 'react-redux';
import { CSSTransition} from 'react-transition-group';
import {actions} from './store/index'; 
import {
    HeaderH,
    HeaderBody,
    LogoDemo,
    Nav,
    NavItem,
    SearchDemo,
    SearchInfo,
    SearchInfoTile,
    SearchInfoTileRight,
    SearchItemBox,
    SearchInfoItem,
    Search,
    Btnview,
    Button
} from './style'


 class Header extends Component{
     render(){
         const { Focus, onFocus, onBlur} = this.props;
        return(
            <HeaderH>
                <HeaderBody>
                    <LogoDemo/>
                    <Nav>
                        <NavItem className='left'>首页</NavItem>
                        <NavItem className='left'>下载App</NavItem>
                        <NavItem>
                            <SearchDemo>
                                <CSSTransition
                                    in={Focus}
                                    timeout={200}
                                    classNames='Searchcss'
                                    >
                                    <Search
                                    onFocus={onFocus}
                                    onBlur={onBlur}
                                    className={Focus?'focus':''}></Search>
                                </CSSTransition>
                                <i className={Focus?'focuBg iconfont icon-sousuo':'iconfont icon-sousuo'}></i>
                                {this.getSearch()}
                            </SearchDemo>
                        </NavItem> 
                        <NavItem className='right'>
                            <i className='iconfont icon-Aa'></i>
                        </NavItem>
                        <NavItem className='right'>登录</NavItem>
                    </Nav>
                    <Btnview>
                        <Button className='reg'>注册</Button>
                        <Button className='book'>
                        <i className='iconfont icon-maobi'></i>
                            写文章
                        </Button>
                    </Btnview>  
                </HeaderBody>
            </HeaderH>
        )
        
     }
     getSearch=()=>{
         const { List,Focus,MouseEnterBtn,MouseLeaveBtn} =this.props;
        if(Focus){
            return (
            <SearchInfo
                onMouseEnter={MouseEnterBtn}
                onMouseLeave={MouseLeaveBtn}
            >
                <SearchInfoTile>
                    搜索
                    <SearchInfoTileRight>换一批</SearchInfoTileRight>
                </SearchInfoTile>
                <SearchItemBox>
                    {
                        List.map((item)=>{
                           return <SearchInfoItem key={item}>{item}</SearchInfoItem>
                        })
                    }
                </SearchItemBox>
            </SearchInfo>
            )
        }else{
            return null
        }
    }
 }

const mapStateToProps=(state)=>({
    //因为是immutable格式的数据，所以要用get方法
    //具体方法可查阅immutable
    Focus:state.getIn(['Header','Focus']),
    List:state.getIn(['Header','list']),
    mouseIn:state.getIn(['Header','mouseIn'])
})
const mapDispatchToProps=(dispatch)=>({
    onFocus(){
        dispatch(actions.getList())
        dispatch(actions.showSearch())
    },
    onBlur(){
        dispatch(actions.closeSearch());
    },
    MouseEnterBtn(){
        dispatch(actions.mousesAction)
        console.log('鼠标移入')
    },
    MouseLeaveBtn(){
        console.log('鼠标移出')
    }
})



export default connect(mapStateToProps,mapDispatchToProps)(Header);
