import React ,{ Component }from 'react';
import { connect } from 'react-redux';
import { CSSTransition} from 'react-transition-group';
import {actions} from './store/index'; 
import {
    HeaderH,
    HeaderBody,
    LogoDemo,
    Nav,
    NavItem,
    SearchDemo,
    SearchInfo,
    SearchInfoTile,
    SearchInfoTileRight,
    SearchItemBox,
    SearchInfoItem,
    Search,
    Btnview,
    Button
} from './style'


 class Header extends Component{
     render(){
        return(
            <HeaderH>
                <HeaderBody>
                    <LogoDemo/>
                    <Nav>
                        <NavItem className='left'>首页</NavItem>
                        <NavItem className='left'>下载App</NavItem>
                        <NavItem>
                            <SearchDemo>
                                <CSSTransition
                                    in={this.props.Focus}
                                    timeout={200}
                                    classNames='Searchcss'
                                    >
                                    <Search
                                    onFocus={this.props.onFocus}
                                    onBlur={this.props.onBlur}
                                    className={this.props.Focus?'focus':''}></Search>
                                </CSSTransition>
                                <i className={this.props.Focus?'focuBg iconfont icon-sousuo':'iconfont icon-sousuo'}></i>
                                {this.getSearch(this.props.Focus)}
                            </SearchDemo>
    
                        </NavItem> 
                        
                        <NavItem className='right'>
                            <i className='iconfont icon-Aa'></i>
                        </NavItem>
                        <NavItem className='right'>登录</NavItem>
                        
                    </Nav>
                    <Btnview>
                        <Button className='reg'>注册</Button>
                        <Button className='book'>
                        <i className='iconfont icon-maobi'></i>
                            写文章
                        </Button>
                    </Btnview>  
                </HeaderBody>
            </HeaderH>
        )
        
     }
     getSearch=(show)=>{
        if(show){
            return (
            <SearchInfo>
                <SearchInfoTile>
                    搜索
                    <SearchInfoTileRight>换一批</SearchInfoTileRight>
                </SearchInfoTile>
                <SearchItemBox>
                    <SearchInfoItem>
                        教育
                    </SearchInfoItem>
                    <SearchInfoItem>
                        教育
                    </SearchInfoItem>
                </SearchItemBox>
            </SearchInfo>
            )
        }else{
            return null
        }
    }
    
 }



const mapStateToProps=(state)=>({
    //因为是immutable格式的数据，所以要用get方法
    //具体方法可查阅immutable
    Focus:state.getIn(['Header','Focus'])
})
const mapDispatchToProps=(dispatch)=>({
    onFocus(){
        dispatch(actions.getList());
        dispatch(actions.showSearch());
    },
    onBlur(){
        dispatch(actions.closeSearch());
    }
})



export default connect(mapStateToProps,mapDispatchToProps)(Header);
